import React from 'react';
import { useParams } from 'react-router-dom';

const options = {
  'French Fries': [{ name: 'Small Size', price: 0 }, { name: 'Medium Size', price: 10 }, { name: 'Large Size', price: 20 }],
  'Fizzy Drink': [{ name: 'Cola', price: 0 }, { name: 'Fanta', price: 0 }, { name: 'Sprite', price: 0 }],
  'Onion Rings': [{ name: 'Single Portion', price: 0 }, { name: 'Double Portion', price: 15 }],
  'Cheeseburger': [{ name: 'No Extra Cheese', price: 0 }, { name: 'Add Extra Cheese', price: 20 }],
  'Ice Cream': [{ name: 'Vanilla', price: 0 }, { name: 'Chocolate', price: 0 }, { name: 'Strawberry', price: 5 }, { name: 'Mint', price: 10 }]
};

function MenuItemPage({ menuItems }) {
  const { itemName } = useParams();
  const item = menuItems.find(item => item.name === decodeURIComponent(itemName));

  return (
    <div className="menuItemPage">
      <img src={item.imageURL} alt={item.name} />
      <h1>{item.name}</h1>
      <p>{item.price}</p>
      <button>Add to Basket</button>
      {options[item.name] && options[item.name].map(option => (
        <div key={option.name}>
          <p>{option.name}</p>
          <p>+{option.price} TL</p>
        </div>
      ))}
    </div>
  );
}

export default MenuItemPage;
