import React, { createContext, useState } from "react";

export const BasketContext = createContext();

export function BasketProvider({ children }) {
    const [basketItems, setBasketItems] = useState([]);

    const addToBasket = (item, selectedOption) => {
      const newBasketItems = [
        ...basketItems,
        {
          ...item,
          selectedOption: selectedOption ? selectedOption.name : 'Standard',
          quantity: 1,
          totalPrice: item.price + (selectedOption ? selectedOption.updatedPrice : 0),
        },
      ];

      setBasketItems(newBasketItems);

      console.log(newBasketItems);
    };

    return (
      <BasketContext.Provider value={{ basketItems, addToBasket }}>
        {children}
      </BasketContext.Provider>
    );
  }
